let get_Time = (note) =>{
  return new Date(note.created).toLocaleDateString()
}

const ListItem = ({note}) => {
  return (
    <Link to={`/note/${note.id}`}>
      <div className='notes-list-item'>
      <h3>{get_Title(note)}</h3>
      <p><span>{get_Time(note)}</span></p>
      </div>
    </Link>
  )
}

export default ListItem
